export const getAnimes = () => {
  return dispatch => {
    dispatch({ type: "LOADING" })
    fetch("http://localhost:3001/animes")
      .then(resp => resp.json())
      .then(animes => dispatch({ type: "SET_ANIMES", animes }))
  }
}

export const addAnime = (anime, history) => {
  return dispatch => {
    fetch('http://localhost:3001/animes', {
      method: "POST",
    })
  }
}
