const initialState = {
    animes: [],
    loading: true
}

const animesReducer = (state=initialState, action) => {
    switch(action.type) {
        case "LOADING":
            return{
                ...state,
                loading: true
            }
        case "SET_ANIMES":
            return {
                ...state,
                loading: false,
                animes: action.animes
            }
        case "ADD_ANIMES":
            return {
                ...state,
                animes: [...state.animes, action]
            }
        default:
            return state;
    }
}

export default animesReducer;
